export function obtenerYears(year){
    return new Date().getFullYear() - year;
}

export function calcularMarca(marca){
    let incremento;
    switch (marca) {
        case 'asiatico':
            incremento = 1.05;
            break;
        case 'europeo' : 
            incremento = 1.30;
            break;
        case 'americano': 
            incremento = 1.15;
            break;
        default:
            break;
    }

    return incremento;
}
