import React, {Fragment} from 'react'
import styled from "@emotion/styled"

const CResumen = styled.div`
    padding: 1rem;
    text-align: center;
    background-color: #00838f;
    color: #fff;
    margin-top: 1rem;
`

const Resumen = ({datos}) => {

    const {marca, year, plan} = datos.datos;
    if (marca === '' || year === '' || plan ==='') {
        return null;
    }

    return (
        <CResumen>
            <h2>Resultado</h2>
            <p>Marca: </p>
            <p>Anho: </p>
            <p>Plan: </p>
        </CResumen>
    );
}

export default Resumen;
