import React, { Fragment } from "react";
import styled from "@emotion/styled";

const Mensaje = styled.p`
    background-color: rgb(127, 224, 237);
    margin-top: 2rem;
    padding: 1rem;
    text-align: center;
`
const Resultado = ({ resultado }) => {

    return (
      (resultado === 0) 
        ? <Mensaje>Seleccione la marca y el anho de su vehiculo junto con el plan que desea contratar</Mensaje> 
        :<p>Costo del Seguro: {resultado} </p>
    );
};

export default Resultado;
